var usersOnline = 500;

//===========================[ Global Script ]===================================

//------------------------------[ Document ]-------------------------------------

$(document).ready(function () {
  //---------------------------[ Extend jQuery ]---------------------------------

  $.fn.extend({
    animateCss: function (animationName, callback) {
      var animationEnd = (function (el) {
        var animations = {
          animation: "animationend",
          OAnimation: "oAnimationEnd",
          MozAnimation: "mozAnimationEnd",
          WebkitAnimation: "webkitAnimationEnd",
        };

        for (var t in animations) {
          if (el.style[t] !== undefined) {
            return animations[t];
          }
        }
      })(document.createElement("div"));

      this.addClass("animated " + animationName).one(animationEnd, function () {
        $(this).removeClass("animated " + animationName);

        if (typeof callback === "function") callback();
      });

      return this;
    },
  });

  //----------------------------[ Connect Account ]------------------------------

  $(".btn-generator").click(function () {
    var player_name = $(".usernameInput").val();

    if (player_name == "" || player_name.replace(/ /g, "").length < 4) {
      $(".usernameInput").animateCss("shake");
    } else {
      $(".gen-txt--module--gen").html(
        '<i class="fas fa-spin fa-circle-notch"></i> Connecting'
      );
      $(".SignUp-module--button--CvL3Q--gen").attr("disabled", "true");

      setTimeout(function () {
        $(".generator-green").css("background", "#09e041");

        $(".gen-txt--module--gen").html(
          '<i class="fas fa-check"></i> Connected'
        );
        $(".SignUp-module--button--CvL3Q--gen").addClass("animated bounceIn");

        setTimeout(function () {
          $(".generator-page").fadeOut(function () {
            $(".generator_packages").show();
          });
        }, 1500);
      }, 3000);
    }
  });

  //----------------------------[ Packages Select ]------------------------------

  $(".packages li").click(function () {
    $(".packages li").removeClass("packages-active");
    $(this).addClass("packages-active");

    $("#generatorModal").modal({ backdrop: "static", keyboard: false });

    $(".console-processing-msg")
      .addClass("bounceIn animated")
      .one(
        "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
        function () {
          $(".console-processing-msg").removeClass("bounceIn animated");
        }
      );

    setTimeout(function () {
      $(".console-processing-msg").html(
        '<i class="fas fa-spin fa-circle-notch"></i>Setting up files...'
      );

      $(".console-processing-msg").removeClass("bounceIn animated");

      $(".console-processing-msg")
        .addClass("bounceIn animated")
        .one(
          "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
          function () {
            $(".console-processing-msg").removeClass("bounceIn animated");
          }
        );

      setTimeout(function () {
        $(".console-processing-msg").html(
          '<i class="fas fa-spin fa-circle-notch"></i>Establishing connection...'
        );

        $(".console-processing-msg").removeClass("bounceIn animated");

        $(".console-processing-msg")
          .addClass("bounceIn animated")
          .one(
            "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
            function () {
              $(".console-processing-msg").removeClass("bounceIn animated");
            }
          );

        setTimeout(function () {
          $(".console-processing-msg").html(
            '<i class="fas fa-spin fa-cog"></i> Downloading Valorant...'
          );

          $(".console-processing-msg").removeClass("bounceIn animated");

          $(".console-processing-msg")
            .addClass("bounceIn animated")
            .one(
              "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
              function () {
                $(".console-processing-msg").removeClass("bounceIn animated");
              }
            );

          setTimeout(function () {
            $(".console-processing-msg").addClass(
              "console-processing-msg-success"
            );
            $(".console-processing-msg").html(
              '<i class="far console-processing-msg-success fa-check-circle"></i> DOWNLOAD COMPLETED'
            );

            $(".console-processing-res-wrap").hide();
            $(".console-processing-msg").show();

            $(".console-processing-msg")
              .addClass("bounceIn animated")
              .one(
                "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
                function () {
                  $(".console-processing-msg").removeClass("bounceIn animated");
                }
              );

            setTimeout(function () {
              $(".console-processing-msg").removeClass(
                "console-processing-msg-success"
              );

              $(".console-processing-msg").html(
                '<i class="fas fa-spin fa-cog"></i> Performing Human Verification...'
              );

              $(".console-processing-msg").removeClass("bounceIn animated");

              $(".console-processing-msg")
                .addClass("bounceIn animated")
                .one(
                  "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
                  function () {
                    $(".console-processing-msg").removeClass(
                      "bounceIn animated"
                    );
                  }
                );

              setTimeout(function () {
                $(".console-processing-msg").addClass(
                  "console-processing-msg-fail"
                );

                $(".console-processing-msg").html(
                  '<i class="fas fa-ban"></i> Human Verification failed'
                );

                $(".console-processing-msg").removeClass("bounceIn animated");

                $(".console-processing-msg")
                  .addClass("bounceIn animated")
                  .one(
                    "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
                    function () {
                      $(".console-processing-msg").removeClass(
                        "bounceIn animated"
                      );
                    }
                  );

                $("#v-e-i-username").html(
                  '<img src="img/user-icon.png"> ' + $(".usernameInput").val()
                );
                $("#v-e-i-vbucks").html(
                  '<i style="font-size: 25px; padding-right: 4px;" class="fas fa-mobile"></i>' +
                    $(".packages-active").attr("data-amount")
                );

                setTimeout(function () {
                  $("#generatorModal").hide();
                  $(".modal-backdrop ").removeClass();

                  $("#verificationModal").modal({
                    backdrop: "static",
                    keyboard: false,
                  });
                }, 1500);
              }, 2000);
            }, 2000);
          }, 2000);
        }, 4000);
      }, 2000);
    }, 2000);
  });

  //----------------------------[ Content Locker ]---------------------------------

  $(".btn-verify").click(function () {
          // window.location.href = "https://verifyhere.xyz/cl/i/6dxw9m";
            window.location.href="https://allgameshackintermediate.onrender.com";
  });
});
